html:
 {% set quotation = doc.name %}
 <div class="letter-head" style="">
    	{{ frappe.render_template(frappe.db.get_value("Letter Head","SABA Default", "content"), {"doc": doc}) }}
    </div>
<h4>BOQ</h4>
<table class="table table-sm  table-bordered table-condensed">
    <thead>
        <tr>
        <th scope="col">Item Code</th> 
        <th scope="col">Description</th>
        <th scope="col">Quantity</th>
		<th scope="col">Stock UOM	</th>

        </tr>
    </thead>
    <tbody>
        {% set items1 = frappe.get_all('Quotation Item', filters={'parent': quotation}, fields=['idx','item_code', 'description','count(qty) as count' ,'sum(qty) as sumq','sum(rate) as sum_rate','section_title','amount','sum(amount) as amounts'],group_by='section_title', order_by='idx asc') %}

        {% set section_titles = [] %}
        
        {%- for item in doc.items  if item.section_title not in section_titles -%}
            {{ section_titles.append(item.section_title) or "" }}
        {%- endfor -%}

        {%- for section in section_titles   -%}
            {% set outerloop = loop.index %}

           
               {%- for item in items1 if item.section_title == section  -%}
                    {%- if section -%}
                    <tr class="styleme{{outerloop}}" style="background:#434141;color: white; text-align: center;">
                        <td colspan="5" style="color: white; "> {{section or '' }}</td>

                    </tr>
                     {%- endif -%}
                {%- endfor -%}
                
                {%- for item in doc.items if item.section_title == section -%}
                     <tr class="styleme{{outerloop}}" >
                        <td>{{ item.item_code }}</td>
                        <td>{{ item.description }}</td>
                        <td>{{ item.qty }}  </td>
                        <td> {{item.stock_uom }} </td>
                    </tr>
                {%- endfor -%}
                {%- for item in items1 if item.section_title == section  -%}
                    <tr class="styleme{{outerloop}}" style="background:#022856;color: white;text-align: right;">
                        <td colspan="5" style="color: #ff9800; ">{{doc.currency}} {{ frappe.format(item.amounts )}}  </td>
                    </tr>
                {%- endfor -%}
        {%- endfor -%}
    </tbody>

</table>
